import styled from 'styled-components';

export const Row = styled.div`
	max-width: 1140px;
	margin-left: auto;
	margin-right: auto;
	margin-top: 20px;
`;

export const VerticalGrid = styled.div`
	display: flex;
	flex-direction: column;
	text-align: center;
`;

export const HorizontalGrid = styled.div`
	display: flex;
	text-align: center;
`;

export const Button = styled.button`
  cursor: pointer;
	width: 20%;
	color: white;
`;
